#Function to find whihc position the pattern occur on the text
def findPattern(text,pattern):
    Pos=[]
    for i in range(len(text)-len(pattern)+1):
        if text[i:i+len(pattern)] == pattern:
            Pos+=[str(i)]
    return Pos

Text = ''
Patt = 'TTTCACTTT'
res = findPattern(Text,Patt)
res = ' '.join(res)
print(res)